// Define the substring to look for
def substring = "Kiev"

// Get the response content from the previous test step
def response = context.expand('${CapitalCity - UA#Response}')

// Check if the substring exists in the response
if (response.contains(substring)) {
    log.info("The response contains the substring: '${substring}'")
    assert true
} else {
    log.error("The response does not contain the substring: '${substring}'")
    assert false, "Substring '${substring}' not found in the response."
}

